ncaa_teams = {}
for url in urls:
	page = requests.get(url)
	tree = html.fromstring(page.content)
	logo = tree.xpath("//img[@class='logo']/@src")[0]
	school = tree.xpath("//h2[@class='team-name']")[0].text_content()
